mapper.select:
SELECT

FROM t_qx_role
WHERE del_flag=0

    and (
    role_name like concat('%',#{searchValue},'%') or
    role_desc like concat('%',#{searchValue},'%')
    )


    ORDER BY ${orderColumn} ${orderDir}


    limit #{start} ,#{length}

mapper.update:
UPDATE t_qx_role
SET  del_flag=1
WHERE role_id =#{natrualkey}
