mapper.select:
SELECT
FROM ( SELECT DISTINCT c.resource_id as resource_id FROM t_qx_role_user a INNER JOIN t_qx_role b ON a.role_id = b.role_id AND a.user_id = #{userId} INNER JOIN t_qx_role_resources c ON c.role_id = b.role_id ) k INNER JOIN t_qx_resources d ON d.resource_id = k.resource_id
AND d.type = #{type}
AND d.pid = #{pid}
ORDER BY NO DESC
