mapper.select:
SELECT

FROM t_qx_user t
WHERE del_flag=0

    AND (
    user_name LIKE concat('%',#{searchValue},'%') OR
    user_email LIKE concat('%',#{searchValue},'%')
    )


    ORDER BY ${orderColumn} ${orderDir}


    ORDER BY update_time desc

limit #{start} ,#{length}

mapper.update:
UPDATE t_qx_user t
SET  del_flag=1
WHERE user_id =#{natrualkey}

update t_qx_user t
set
user_pwd = #{password}
where user_id= #{natrualkey}
